mapper.select:
SELECT *
FROM file
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
ORDER BY upd_date DESC

SELECT *
FROM file
WHERE parent_table = #{parentTable}
AND parent_no = #{parentNo}
AND file_code = 0

mapper.update:
UPDATE file
   SET file_name = #{fileName}
      ,origin_name = #{originName}
      ,path = #{path}
      ,file_size = #{fileSize}
      ,file_code = #{fileCode}
      ,upd_date = now()
WHERE parent_no = #{parentNo}
AND parent_table = #{parentTable}

mapper.delete:
DELETE FROM file
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
